    download() {
      if (typeof this.file === 'string') {
        window.open(this.file)
      } else {
        const url = URL.createObjectURL(this.file)
        window.open(url)
      }
    },
  },
}
</script>

<style lang="scss">
.el-file {
  border: 1px solid #dfe2ea;
}
.el-form-item.is-error .el-file {
  border: 1px solid #f56c6c;
}
</style>
