<script>
import axios from 'axios';
export default {
  props: {
    files: '',
    placeholder: {
      type: String,
      default: "Faylni yuklang",
    },
  },
  methods: {
    openNotificationWithIcon(type,message) {
        this.$notification[type]({
            message: 'Diqqat!',
            description: "Pdf yoki rasm 5mb dan oshmasligi kerak",
        });
    },
    updateValue(value) {
      const cookieToken = this.$cookies.get("token")
      const config = (cookieToken) ? {
          headers: {
              Authorization: `Bearer ${cookieToken}`,
          }
      } : {}
      if (value.target.files[0]) {
        if(value.target.files[0].size < 5123071){
          let file = value.target.files[0]
          var formData = new FormData();
          formData.append('file', file);
          axios.post('http://172.18.9.93:8082/api/user/uploadFile',
          formData,
          {
          config,
          }).then(response => {
            if(response.data.success){
              this.$emit('inputDown', response.data.object)
            }
          }).catch(error => {
            this.$emit('inputDown', '')
          });
        } else {
          this.openNotificationWithIcon('error')
          this.$emit('inputDown', '')
        }
      }
    },
    fileClear() {
       this.$emit('inputDown', '')
    },
    download() {
      if (typeof this.files === "string") {
        window.open(this.files);
      } else {
        const url = URL.createObjectURL(this.files);
        window.open(url);
      }
    },
  },
};
</script>
